import React from 'react';

class App extends React.Component {
  constructor() {
    super()

    this.handleClick1 = this.handleClick1.bind(this);
    this.handleClick2 = this.handleClick2.bind(this);
    this.handleClick3 = this.handleClick3.bind(this);
  }

  handleClick1() {
    console.log('Botão foi chamado parça')
  }

  handleClick2() {
    console.log('Botão foi chamado bro')
  }

  handleClick3() {
    console.log('Botão foi chamado amigo')
  }

  render() {
    return (
      <>
      <button onClick={this.handleClick1}>butao1</button>
      <button onClick={this.handleClick2}>butao2</button>
      <button onClick={this.handleClick3}>butao3</button>
      </>
    );
  }
}

export default App;
