import React from 'react';

function handleClick1 () {
  console.log('Botão foi chamado parça')
}

function handleClick2 () {
  console.log('Botão foi chamado irmão')
}

function handleClick3 () {
  console.log('Botão foi chamado lek')
}

class App extends React.Component {
  render() {
    return (
      <>
      <button onClick={handleClick1}>butao1</button>
      <button onClick={handleClick2}>butao2</button>
      <button onClick={handleClick3}>butao3</button>
      </>
    );
  }
}

export default App;
