import React from 'react';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.setState = {
      personalObj: [],
      loading: true,
    }
  }

  async personalFetch() {
    this.setState(
      {loading: true}, async () => {
        const fetch = await fetch('https://api.randomuser.me/');
        const data = await fetch.json();
        this.setState({
          loading: true,
          personalObj: data,
        })
      }
    )
  }

  componentDidMount() {
    this.personalFetch();
  }

  render() {
    const { personalObj, loading } = this.state;
    const loadingElement = <span>Carregando...</span>

    return(
      <div>
        <p>
          { loading ?? loadingElement }
        </p>
      </div>
    )
  }
}

export default App;
