import React from 'react';
import Loading from './Loading';

class PersonDetails extends React.Component {
  constructor() {
    super();

    this.setState = {
      person: [],
      loading: true,
    }
  }

  async personalFetch() {
    const url = 'https://api.randomuser.me/';
    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        this.setState({
          person: data.results,
          loading: false,
        });
      });
  }

  componentDidMount() {
    this.personalFetch();
