import React from 'react';
import Loading from './Loading';

class PersonDetails extends React.Component {
  constructor() {
    super();

    this.setState = {
      personalObj: [],
      loading: true,
    }
  }

  async personalFetch() {
    this.setState(
      {loading: true}, async () => {
        const fetchh = await fetch('https://api.randomuser.me/');
        const data = await fetchh.json();
        this.setState({
          loading: false,
          personalObj: data.results,
        })
      }
    )
  }

  componentDidMount() {
    this.personalFetch();
  }

